mapper.select:
SELECT round((COUNT(CASE WHEN win ='True' THEN 1
END)
/ COUNT(*) * 100),2)
AS win_percentage
FROM zzz_datas_rune
WHERE
championName_KR = #{championName_KR} 
and teamPosition = #{teamPosition} 
and tier1 = #{tier1}

SELECT round(((COUNT(CASE WHEN banChampionName_KR = #{championName_KR} THEN 1 END ) / (COUNT(*)/10))
 * (COUNT(DISTINCT CASE WHEN teamPosition = #{teamPosition} and championName_KR = #{championName_KR} THEN match_id END) / COUNT(DISTINCT case when championName_KR = #{championName_KR} then match_id end)) * 100),2) AS ban_percentage
FROM zzz_datas_detail where tier1 = #{tier1};
